import React, { useState, useRef, useEffect } from 'react';

const Popup = ({ onClose, onSubmit }) => {
  const currentDate = new Date().toISOString().slice(0, 16);
  const [noteTitle, setNoteTitle] = useState('');
  const [birthDate, setBirthDate] = useState(new Date());
  const [deathDate, setDeathDate] = useState(currentDate);
  const [image, setImage] = useState('');
  const textInput = useRef(null);

  useEffect(() => {
    textInput.current.focus();
  }, []);

  const handleNoteTitleChange = (event) => {
    setNoteTitle(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const newNote = { id: Date.now(), title: noteTitle, born: birthDate, died: deathDate, image: image };
    onSubmit(newNote);
    onClose();
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImage(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleInputChange = (event) => {
    setBirthDate(new Date(event.target.value));
  };

  const handleDiedDateChange = (event) => {
    setDeathDate(event.target.value);
  };

  return (
    <div className="popupBox">
      <button className="closeBtn" onClick={onClose}>X</button>
      <h2>Add a new Note</h2>
      <p>Please enter the title of your note:</p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="fileUpload">
          <img className="add-image-button" src="your-image-url.jpg" alt="Add image button" />
        </label>
        <div className="input-wrapper">
          <input id="fileUpload" type="file" accept="image/*" onChange={handleFileUpload} style={{ display: "none" }} />
          <input type="text" value={noteTitle} onChange={handleNoteTitleChange} placeholder="Name of the deceased" ref={textInput} />
        </div>
        <div className="date-inputs">
          <div className="born-input">
            <p3 className="label-text">Born:</p3>
            <input type="datetime-local" step="1" value={birthDate} onChange={handleInputChange} className="date-time-input" />
          </div>
          <div className="died-input">
            <p3 className="label-text">Died:</p3>
            <input
              type="datetime-local"
              value={deathDate}
              onChange={handleDiedDateChange}
              className="date-time-input"
              step="1"
            />
          </div>
        </div>
        <button type="submit">Add Note</button>
      </form>
    </div>
  );
};

export default Popup;
