import React from 'react';
import Empty from './Empty';
import FormattedDate from './FormattedDate';
import './Layout.css';

class Layout extends React.Component {
  constructor(props) {
    super(props);
    const currentDate = new Date().toISOString().slice(0, 16);
    this.state = {
      showPopup: false,
      notes: JSON.parse(localStorage.getItem('notes')) || [],
      noteTitle: '',
      birthDate: new Date(),
      deathDate: currentDate,

      userInput: '',
    };
    this.handleAddButtonClick = this.handleAddButtonClick.bind(this);
    this.handlePopupClose = this.handlePopupClose.bind(this);
    this.handleNoteTitleChange = this.handleNoteTitleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.deleteNote = this.deleteNote.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleDiedDateChange = this.handleDiedDateChange.bind(this);
    this.handleFileUpload = this.handleFileUpload.bind(this);

  }

  handleAddButtonClick() {
    document.querySelector("body").style.overflow = "hidden";
    this.setState({
      showPopup: true
    });
  }

  handlePopupClose() {
    const currentDate = new Date().toISOString().slice(0, 16);
    document.querySelector("body").style.overflow = "auto";
    this.setState({
      showPopup: false,
      noteTitle: '',
      birthDate: new Date(),
      deathDate: currentDate, // Update this line
      userInput: '',
    });
  }
  

  handleNoteTitleChange(event) {
    this.setState({
      noteTitle: event.target.value
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    const { notes, noteTitle, birthDate, deathDate, image } = this.state;
    const newNote = { id: notes.length + 1, title: noteTitle, born: birthDate, died: deathDate, image: image }; // Add image: image here
    const newNotes = [...notes, newNote];
    this.setState({
      notes: newNotes,
      showPopup: false,
      noteTitle: '',
      birthDate: new Date(),
      deathDate: new Date(),
      userInput: '',
      image: ''
    });
    localStorage.setItem('notes', JSON.stringify(newNotes));
  }
  
  

handleFileUpload(event) {
  const file = event.target.files[0];
  const reader = new FileReader();
  reader.onloadend = () => {
    this.setState({ image: reader.result });
  };
  reader.readAsDataURL(file);
}

  deleteNote(id) {
    const { notes } = this.state;
    const updatedNotes = notes.filter(note => note.id !== id);
    this.setState({ notes: updatedNotes });
    localStorage.setItem('notes', JSON.stringify(updatedNotes));
  }

  handleInputChange(event) {
    this.setState({ birthDate: new Date(event.target.value) });
  }

  handleDiedDateChange(event) {
    this.setState({ deathDate: event.target.value });
  }

  componentDidUpdate() {
    if (this.state.showPopup) {
      this.textInput.focus();
    }
  }
  render() {
    const { showPopup, noteTitle, notes, birthDate, deathDate, userInput } = this.state;
    return (
      <div>
        <header>
          <aside>&nbsp;</aside>

          <div id="app-header">
            <h1>
              Lotion
            </h1>
          </div>

          <aside>
            <button className="add-note-btn" onClick={this.handleAddButtonClick}>+ new Obiturary</button>
          </aside>
        </header>

        {notes.length === 0 && <Empty />}
        {showPopup && (
  <div className="popupBox">
    <button className="closeBtn" onClick={this.handlePopupClose}>X</button>
    <h2>Add a new Note</h2>
    <p>Please enter the title of your note:</p>
    <form onSubmit={this.handleSubmit}>
      <label htmlFor="fileUpload">
        <img src="your-image-url.jpg" alt="Add image button" style={{ marginBottom: "10px", cursor: "pointer" }} />
      </label>
      <div style={{ display: "flex" }}>
        <div style={{ position: "relative" }}>
          <input id="fileUpload" type="file" accept="image/*" onChange={this.handleFileUpload} style={{ display: "none" }} />
          <input type="text" value={noteTitle} onChange={this.handleNoteTitleChange} placeholder="Name of the deceased" ref={(input) => { this.textInput = input; }} />
        </div>
      </div>
      <div style={{ display: "flex" }}>
        <div style={{ display: "flex", alignItems: "center", marginRight: "10px" }}>
          <p3 style={{ marginRight: "5px" }}>Born:</p3>
          <input type="datetime-local" step="1" value={birthDate} onChange={this.handleInputChange} className="date-time-input" />
        </div>
        <div style={{ display: "flex", alignItems: "center" }}>
          <p3 style={{ marginRight: "5px" }}>Died:</p3>
          <input
  type="datetime-local"
  value={deathDate}
  onChange={this.handleDiedDateChange}
  className="date-time-input"
  step="1"
/>
        </div>
        
      </div>
      <button type="submit">Add Note</button>
    </form>
  </div>
)}




<div className="notes">
  {notes.map(note => (
    <div key={note.id} className="note">
      {note.image && <img src={note.image} alt="Note" style={{ maxWidth: "100%", height: "auto" }} />}
      <div className="details">
        <p>{note.title}</p>
        <p>Born: {new Date(note.born).toLocaleString()}</p> {/* Add this line */}
        <p>Died: {new Date(note.died).toLocaleString()}</p> {/* Add this line */}
      </div>
      <div className="bottom-content">
        <div className="settings">
          <i onClick={this.showMenu} className="uil uil-ellipsis-h"></i>
          <ul className="menu">
            <li onClick={() => this.updateNote(note.id, note.title)}><i className="uil uil-pen"></i>Edit</li>
            <li onClick={() => this.deleteNote(note.id)}><i className="uil uil-trash"></i>Delete</li>
          </ul>
        </div>
        <button className="delete-btn" onClick={() => this.deleteNote(note.id)}>Delete</button>
      </div>
    </div>
  ))}
</div>

      </div>
    );
  }
}

   export default Layout
