import { useState } from "react";

function App() {
  const [popup, setPopup] = useState(false);

  const handleClick = () => {
    setPopup(!popup);
  };

  return (
    <div id="container">
      <div class = "navbar">

      <h1 class = "title">The Last Show</h1>

      <button class="popup" onClick={handleClick}>
        + New Obituary
      </button>
      </div>

      {popup && (
        <div id="popup-container">
          <div class="popup-content">
          <p>Create a New Obituary </p>
          {/* <img id ="header-border" src="header.jpg" alt="logo" /> */}
          <input type="text">Name of the decesed</input>


          <button class="close-popup" onClick={handleClick}>X</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
